import React, { useState } from "react";
import Note from "./components/Note";

const App = props => {
  const [notes, setState] = useState(props.notes);
  const [newNoteContent, setNewNoteContent] = useState("add note");

  const handleTextInput = e => {
    setNewNoteContent(e.target.value);
  };

  const handleSubmit = e => {};

  const renderNotes = () => {
    return notes.map(({ content, id, date, important }) => (
      <Note key={id} content={content} date={date} important={important} />
    ));
  };

  return (
    <div className="App">
      <h1>Notes</h1>
      <form onSubmit={handleSubmit}>
        <input onChange={handleTextInput} type="text" value={newNote} />
        <input type="submit" />
      </form>
      <ul>{renderNotes()}</ul>
    </div>
  );
};

export default App;
