import React from "react";

const Note = ({ key, content, important, toggleImportant, id }) => {
  const styles = {
    textDecoration: "underline"
  };

  const handleClick = id => {
    toggleImportant(id);
  };

  return (
    <li key={key} style={important ? styles : null}>
      {content}{" "}
      <button onClick={handleClick}>
        {important ? "make not important" : "important"}
      </button>
    </li>
  );
};
export default Note;
