import React from "react";
import "../styles/index.css";

const Note = ({ content, important, toggleImportant, id, deleteNote }) => {
  const styles = {
    textDecoration: "underline"
  };

  const handleClick = () => {
    // console.log(id);
    toggleImportant(id);
  };

  const handleDelete = e => {
    deleteNote(id);
  };

  return (
    <li className="Note" key={id} id={id} style={important ? styles : null}>
      <button
        className={important ? "red-button" : "button"}
        onClick={handleClick}
      >
        {important ? "make not important" : "mark important"}
      </button>
      {content} <button onClick={handleDelete}>delete</button>
    </li>
  );
};
export default Note;
