import React, { useState } from "react";
import Note from "./components/Note";

const App = props => {
  const [notes, setNotes] = useState(props.notes);
  const [showAll, setShowAll] = useState(true);

  const importantNotes = notes.filter(note => note.important);

  const [note, setNote] = useState("add note");

  const handleTextInput = e => {
    setNote(e.target.value);
  };

  const generateNoteId = () => {
    // insures unique ID by adding one to highest existing
    return Math.max(...notes.map(note => note.id)) + 1;
  };

  const handleSubmit = e => {
    e.preventDefault();
    const newNote = {
      content: note,
      important: Math.random() > 0.5,
      date: new Date(),
      id: generateNoteId()
    };

    setNotes(notes.concat(newNote));
  };

  const toggleImportant = () => {
    // only important notes displayed showAll is false. toggled by button
    setShowAll(!showAll);
  };

  const generalRender = noteArr =>
    noteArr.map(({ content, id, date, important }) => (
      <Note key={id} content={content} date={date} important={important} />
    ));

  return (
    <div className="App">
      <h1>Notes</h1>
      <button onClick={toggleImportant}>
        {showAll ? "Click to Show only important" : "Click to Show All"}
      </button>
      <form onSubmit={handleSubmit}>
        <input onChange={handleTextInput} type="text" value={note} />
        <input type="submit" />
      </form>
      <ul>{showAll ? generalRender(notes) : generalRender(importantNotes)}</ul>
    </div>
  );
};

export default App;
