import React, { useState, useEffect } from "react";
import axios from "axios";
import Note from "./components/Note";

const App = props => {
  const [notes, setNotes] = useState([]);
  const [showAll, setShowAll] = useState(true);

  const importantNotes = notes.filter(note => note.important);

  const [note, setNote] = useState("add note");

  useEffect(() => {
    console.log("useEffect triggered this");
    axios
      .get("http://localhost:3001/notes")
      .then(res => {
        console.log(res);
        setNotes(res.data);
      })
      .catch(err => console.log(err.message));
  }, []);

  const handleTextInput = e => {
    setNote(e.target.value);
  };

  const generateNoteId = () => {
    // insures unique ID by adding one to highest existing
    return Math.max(...notes.map(note => note.id)) + 1;
  };

  const handleSubmit = e => {
    e.preventDefault();
    const newNote = {
      content: note,
      important: Math.random() > 0.5,
      date: new Date(),
      id: generateNoteId()
    };

    axios
      .post("http://localhost:3001/notes", newNote)
      .then(res => console.log(res));
    // add to state
    setNotes(notes.concat(newNote));
  };

  const showImportant = () => {
    // only important notes displayed showAll is false. toggled by button
    setShowAll(!showAll);
  };

  const toggleImportant = id => {
    // find note by id
    const note = notes.find(note => note.id === +id);
    console.log(id);
  };

  const generalRender = noteArr =>
    noteArr.map(({ content, id, date, important }) => (
      <Note
        key={id}
        content={content}
        date={date}
        important={important}
        toggleImportant={toggleImportant}
        id={id}
      />
    ));

  return (
    <div className="App">
      <h1>Notes</h1>
      <button onClick={showImportant}>
        {showAll ? "Click to Show only important" : "Click to Show All"}
      </button>
      <form onSubmit={handleSubmit}>
        <input onChange={handleTextInput} type="text" value={note} />
        <input type="submit" />
      </form>
      <ul>{showAll ? generalRender(notes) : generalRender(importantNotes)}</ul>
    </div>
  );
};

export default App;
