import React, { useState } from "react";
import Note from "./components/Note";

const App = props => {
  const [notes, setNotes] = useState(props.notes);

  // control form
  const [note, setNote] = useState("add note");

  const handleTextInput = e => {
    setNote(e.target.value);
  };

  // generate note UID
  const generateNoteId = () => {
    return Math.max(...notes.map(note => note.id)) + 1;
  };

  const handleSubmit = e => {
    e.preventDefault();
    const newNote = {
      content: note,
      important: false,
      date: new Date(),
      id: generateNoteId()
    };

    setNotes(notes.concat(newNote));
  };

  const renderNotes = () => {
    return notes.map(({ content, id, date, important }) => (
      <Note key={id} content={content} date={date} important={important} />
    ));
  };

  return (
    <div className="App">
      <h1>Notes</h1>
      <form onSubmit={handleSubmit}>
        <input onChange={handleTextInput} type="text" value={note} />
        <input type="submit" />
      </form>
      <ul>{renderNotes()}</ul>
    </div>
  );
};

export default App;
