import React from "react";

const Note = ({ content, important, toggleImportant, id }) => {
  const styles = {
    textDecoration: "underline"
  };

  const handleClick = () => {
    // console.log(id);
    toggleImportant(id);
  };

  return (
    <li className="Note" key={id} id={id} style={important ? styles : null}>
      {content}{" "}
      <button onClick={handleClick}>
        {important ? "make not important" : "important"}
      </button>
    </li>
  );
};
export default Note;
